package codes;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=0;
		if(n>0)
			System.out.println("Positive number");
		else if(n<0)
			System.out.println("Negative Number");
		else
			System.out.println("Neither positive   nor negative");
	}

}
