package codes;

public class Salary {

	public Salary() {
		// TODO Auto-generated constructor stub
		System.out.println("Constructor");
		
	}
	void salary() {
		System.out.println("Your Salary is default 10000");
		
	}
	void salary(int a) {
		System.out.println("Your salary is "+ a);
	}
	void salary(int a,int b) {
		System.out.println("Your salary is "+(a+b));
	}
	

}
