package standrews;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=5;
		for(int i=1 ; i<=10;  i++) {
			System.out.println(num+"*"+i+"="+i*num);
		}
		
		int i=1;//initialize
		while(i<=10) {//condition
			System.out.println("5*"+i+"="+i*5);
			i++;//change or increment
		}
			
	}

}



